    private void btnInserirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInserirActionPerformed

        PessoaDAO dao = new PessoaDAO();
        Pessoa p = new Pessoa();
        Fisica f = new Fisica();
        Juridica j = new Juridica();

        p.setNome(txtNome.getText());
        f.setRg(Integer.parseInt(txtRg.getText()));
        f.setCpf(Integer.parseInt(txtCpf.getText()));
        p.setDataNascimento(txtDataNascimento.getText());
        f.setNomeDaMae(txtNomeMae.getText());
        f.setNomeDoPai(txtNomePai.getText());

        dao.create(p, f, j);

        txtId.setText("");
        txtNome.setText("");
        txtRg.setText("");
        txtCpf.setText("");
        txtDataNascimento.setText("");
        txtNomeMae.setText("");
        txtNomePai.setText("");
    }//GEN-LAST:event_btnInserirActionPerformed

    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        if (txtNome.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Permitido somente busca pelo Nome ");

        } else {
            Pessoa p = new Pessoa();
            PessoaDAO dao = new PessoaDAO();


            p = dao.read(txtNome.getText());

            if (p.getNome() != null) {
                txtId.setText(String.valueOf(p.getIdPessoa()));
                txtNome.setText(p.getNome());
                txtRg.setText(String.valueOf(p.getFisica().getRg()));
                txtCpf.setText(String.valueOf(p.getFisica().getCpf()));
                txtDataNascimento.setText(p.getDataNascimento());
                txtNomeMae.setText(p.getFisica().getNomeDaMae());
                txtNomePai.setText(p.getFisica().getNomeDoPai());
            }
        }
    }//GEN-LAST:event_btnPesquisarActionPerformed

    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
        Pessoa p = new Pessoa();
        PessoaDAO dao = new PessoaDAO();
        Fisica f = new Fisica();
        Juridica j = new Juridica();

        p.setIdPessoa(Integer.parseInt(txtId.getText()));
        p.setNome(txtNome.getText());
        f.setRg(Integer.parseInt(txtRg.getText()));
        f.setCpf(Integer.parseInt(txtCpf.getText()));
        p.setDataNascimento(txtDataNascimento.getText());
        f.setNomeDaMae(txtNomeMae.getText());
        f.setNomeDoPai(txtNomePai.getText());


        dao.update(p, f, j);

        txtId.setText(String.valueOf(p.getIdPessoa()));
        txtNome.setText(p.getNome());
        txtRg.setText(String.valueOf(f.getRg()));
        txtCpf.setText(String.valueOf(f.getCpf()));
        txtDataNascimento.setText(p.getFisica().getDataNascimento());
        txtNomeMae.setText(f.getNomeDaMae());
        txtNomePai.setText(f.getNomeDoPai());


    }//GEN-LAST:event_btnAlterarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        Pessoa p = new Pessoa();
        PessoaDAO dao = new PessoaDAO();

        p.setIdPessoa(Integer.parseInt(txtId.getText()));

        dao.delete(p);

        txtId.setText("");
        txtNome.setText("");
        txtRg.setText("");
        txtCpf.setText("");
        txtDataNascimento.setText("");
        txtNomeMae.setText("");
        txtNomePai.setText("");

    }//GEN-LAST:event_btnExcluirActionPerformed

